SELECT a.article_id, a.title, a.content, a.view_count, a.created_at, a.updated_at, c.category_id, c.name AS categoryName, c.board_type, ad.name AS adminName FROM article a INNER JOIN category c ON a.category_id = c.category_id INNER JOIN admin ad ON a.admin_id = ad.admin_id WHERE a.category_id IN
#{categoryId}
AND a.category_id = #{search.category}
AND (title LIKE CONCAT('%', #{search.keyword}, '%') OR ad.name LIKE CONCAT('%', #{search.keyword}, '%') OR content LIKE CONCAT('%', #{search.keyword}, '%'))
AND a.created_at BETWEEN #{search.startDate} AND CONCAT(#{search.endDate}, ' 23:59:59') ORDER BY ${search.sortByName} ${search.sortOrder} LIMIT #{criteria.limit} OFFSET #{criteria.offset}

SELECT count(article_id) FROM article a INNER JOIN category c ON a.category_id = c.category_id INNER JOIN admin ad ON a.admin_id = ad.admin_id WHERE a.category_id IN
#{categoryId}
AND a.category_id = #{category}
AND (title LIKE CONCAT('%', #{keyword}, '%') OR ad.name LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))
AND a.created_at BETWEEN #{startDate} AND CONCAT(#{endDate}, ' 23:59:59')
